app.controller("user_edit",["$scope","$stateParams","userAPIService","dialogService","$rootScope",function(n,t,i,r,u){n.isCreate=t.userId==""?!0:!1;n.user={};n.user.userTypeId=2;n.userTypes=[{name:"订单操作员",id:2},{name:"系统管理员",id:1}];u.invalidMapping={username:"td_m_username",password:"td_m_password",usertype:"td_m_usertype"};n.isCreate||i.getUser(t.userId).then(function(t){n.user=t.data});n.submit=function(){n.isCreate?i.createUser(n.user).then(function(){r.showMention("1","创建用户成功","users")}):i.updateUser(n.user).then(function(){r.showMention("1","修改用户成功","users")})}}]);